#include "../math/Ascii2Bin.h"

int main(int argc, char** argv){
	printf("sanity test to stdout... (.Y.)\n");
	Ascii2Bin* ascii2bin_ptr = new Ascii2Bin();
	std::string tempString(argv[1]);
	ascii2bin_ptr->setAsciiString(tempString);
	ascii2bin_ptr->calculateBinFromAsciiString("veronica ann belmont is beautiful... her full C cup boobs are great, but we can make them better with pixel alchemy; we'll make her a full C++ cup!");
	ascii2bin_ptr->calculateAsciiFromBinString("");
	return 0; //success condition
}


